import Counter from "./Counter";
import { useState } from "react";

function CounterContainer() {
    const [isShow, setisShow] = useState(false);
    return 
        <div>
             <button onClick={() => {setisShow(!isShow)}} >{isShow?'숨기기':'보이기'}</button>
            <Counter />
        </div>;


}


export default CounterContainer
