import React from "react";

export class LikeButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = {liked: false};
        this.text = "좋아요";
    }

    handleClick() {
        this.text = "clicked 좋아요"

    }

    render(){
        return <button onClick={() => this.handleClick}>this.text</button>
    }

}
