function Factorial(props) {
    function factorial(n){
        console.log(`factorial(${n})`);
        return n <= 0 ? 1 : n * factorial(n - 1);
    }

    const result = factorial(props.n);

    return(
        <>
            <h3>{`${props.n} Factorial`}</h3>
            <h4>{result}</h4>
        </>
    )
}
export default Factorial;
