import React from "react";

const styles = {
    wrapper:{
        margin : 8,
        padding: 8,
        display: "flex",
        flexDirection : "row",
        border: "1px solid grey",
        borderRadius: 16,
    },
    messageText: {
        color: "black",
        fontSize: 16,
    },
};


const reservedNotifications = [
    {
        message: "Hello,"
    }, 
    {
        message: "lunchTime"
    },
    {
        message: "presention time 10 min later"
    }
];

class Notification extends React.Component{
    constructor(props) {
        super(props);
        this.state = {};
    }
    render() {
        return (
            <div style={styles.wrapper}>
                <span style={styles.messageText}>{this.props.message}</span>
            </div>
        );
    }
}
export default Notification
