import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ReviewWrite = () => {
  const navigate = useNavigate();

  // 입력값 상태 관리
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  // 로그인 체크 (없으면 리스트로 튕겨내기)
  useEffect(() => {
    if (!localStorage.getItem('userId')) {
      alert('로그인이 필요한 서비스입니다.');
      navigate('/review');
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = {
      memberId: localStorage.getItem('userId'), // 로그인된 ID 사용
      boardTitle: title,
      boardContent: content,
      mvNo: 1 // 테스트를 위해 영화 번호 1번으로 고정 (실제로는 영화 상세페이지에서 받아와야 함)
    };

    try {
      const response = await axios.post('http://localhost:8080/api/review/write', data);
      if (response.data.status === 'success') {
        alert('리뷰가 등록되었습니다!');
        navigate('/review'); // 작성 후 리스트로 이동
      } else {
        alert('등록 실패: ' + response.data.message);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('서버 통신 에러 발생');
    }
  };

  // 스타일 (ReviewList와 동일한 테마)
  const styles = {
    container: {
      backgroundColor: '#2A2A2A',
      minHeight: '100vh',
      padding: '40px',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      color: 'white'
    },
    formCard: {
      backgroundColor: '#333',
      padding: '40px',
      borderRadius: '20px',
      width: '100%',
      maxWidth: '600px',
      boxShadow: '0 10px 20px rgba(0,0,0,0.5)'
    },
    label: {
      display: 'block',
      marginBottom: '10px',
      color: '#53ef12',
      fontWeight: 'bold'
    },
    input: {
      width: '100%',
      padding: '15px',
      marginBottom: '20px',
      borderRadius: '8px',
      border: '1px solid #555',
      backgroundColor: '#222',
      color: 'white',
      fontSize: '1rem'
    },
    textarea: {
      width: '100%',
      padding: '15px',
      marginBottom: '20px',
      borderRadius: '8px',
      border: '1px solid #555',
      backgroundColor: '#222',
      color: 'white',
      fontSize: '1rem',
      height: '150px',
      resize: 'none'
    },
    submitButton: {
      width: '100%',
      backgroundColor: '#53ef12',
      color: '#2A2A2A',
      padding: '15px',
      border: 'none',
      borderRadius: '8px',
      fontWeight: 'bold',
      fontSize: '1.1rem',
      cursor: 'pointer'
    },
    backButton: {
      marginTop: '10px',
      background: 'none',
      border: 'none',
      color: '#aaa',
      cursor: 'pointer',
      textDecoration: 'underline'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.formCard}>
        <h2 style={{ textAlign: 'center', marginBottom: '30px', color: '#53ef12' }}>Review Write</h2>

        <form onSubmit={handleSubmit}>
          <div>
            <label style={styles.label}>제목</label>
            <input 
              style={styles.input}
              type="text" 
              placeholder="리뷰 제목을 입력하세요"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div>
            <label style={styles.label}>내용</label>
            <textarea 
              style={styles.textarea}
              placeholder="영화는 어떠셨나요? 솔직한 감상을 적어주세요."
              value={content}
              onChange={(e) => setContent(e.target.value)}
              required
            />
          </div>

          <button type="submit" style={styles.submitButton}>등록하기</button>
        </form>

        <div style={{ textAlign: 'center' }}>
          <button style={styles.backButton} onClick={() => navigate('/review')}>
            목록으로 돌아가기
          </button>
        </div>
      </div>
    </div>
  );
};

export default ReviewWrite;
