import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ReviewList = () => {
  const [reviews, setReviews] = useState([]);
  const navigate = useNavigate();

  // 로그인 체크 (localStorage에 userId가 있다고 가정)
  // 학습용이므로 테스트할 때 개발자도구 > Application > Local Storage에 userId를 강제로 추가해보세요.
  const isLoggedIn = localStorage.getItem('userId') !== null; 

  // 디자인 스타일 (Atomize 느낌 + 요청 컬러)
  const styles = {
    container: {
      backgroundColor: '#2A2A2A',
      minHeight: '100vh',
      padding: '40px',
      color: 'white',
      fontFamily: 'Helvetica, Arial, sans-serif'
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '30px',
      borderBottom: '1px solid #444',
      paddingBottom: '20px'
    },
    title: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#53ef12' // 포인트 컬러
    },
    writeButton: {
      backgroundColor: '#53ef12',
      color: '#2A2A2A',
      border: 'none',
      padding: '10px 20px',
      borderRadius: '8px', // Atomize 스타일
      fontWeight: 'bold',
      cursor: 'pointer',
      fontSize: '1rem',
      transition: '0.3s'
    },
    card: {
      backgroundColor: '#333',
      padding: '20px',
      marginBottom: '15px',
      borderRadius: '12px', // 둥근 모서리
      boxShadow: '0 4px 6px rgba(0,0,0,0.3)',
      borderLeft: '5px solid #53ef12'
    },
    cardTitle: {
      fontSize: '1.2rem',
      fontWeight: 'bold',
      marginBottom: '10px'
    },
    cardInfo: {
      fontSize: '0.9rem',
      color: '#aaa',
      marginBottom: '10px'
    }
  };

  useEffect(() => {
    // 백엔드에서 데이터 가져오기 (테스트를 위해 영화번호 1번으로 고정)
    axios.get('http://localhost:8080/api/review/list/1')
      .then(response => {
        setReviews(response.data);
      })
      .catch(error => {
        console.error('Error fetching reviews:', error);
      });
  }, []);

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Movie Reviews</h1>
        {/* 로그인 했을 때만 버튼 보임 */}
        {isLoggedIn && (
          <button 
            style={styles.writeButton}
            onClick={() => navigate('/review/write')}
          >
            리뷰 작성하기
          </button>
        )}
      </div>

      <div>
        {reviews.length === 0 ? (
          <p style={{ textAlign: 'center', color: '#777' }}>작성된 리뷰가 없습니다.</p>
        ) : (
          reviews.map((review) => (
            <div key={review.boardNo} style={styles.card}>
              <div style={styles.cardTitle}>{review.boardTitle}</div>
              <div style={styles.cardInfo}>
                작성자: {review.memberId} | 날짜: {new Date(review.regDate).toLocaleDateString()}
              </div>
              <div>{review.boardContent}</div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ReviewList;
