mapper.insert:

    SELECT last_insert_rowid()

INSERT INTO rvboard (
    member_id, board_title, board_content, mv_no
) VALUES (
    #{memberId}, #{boardTitle}, #{boardContent}, #{mvNo}
)

mapper.update:
UPDATE rvboard
SET board_title = #{boardTitle},
    board_content = #{boardContent}
WHERE board_no = #{boardNo}
