import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';
import { findPassword } from '../api/userApi';

const FindPasswordPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [foundPassword, setFoundPassword] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // ÏûÖÎ†• Ïãú ÏóêÎü¨ Î©îÏãúÏßÄ ÌÅ¥Î¶¨Ïñ¥
    if (error) {
      setError('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    // Ïù¥Î¶ÑÍ≥º Ïù¥Î©îÏùº ÏûÖÎ†• ÌôïÏù∏
    if (!formData.name || !formData.email) {
      setError('Ïù¥Î¶ÑÍ≥º Ïù¥Î©îÏùºÏùÑ Î™®Îëê ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      setIsLoading(false);
      return;
    }

    // Ïù¥Î©îÏùº ÌòïÏãù Í≤ÄÏÇ¨
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError('Ïò¨Î∞îÎ•∏ Ïù¥Î©îÏùº ÌòïÏãùÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      setIsLoading(false);
      return;
    }

    try {
      // Spring Boot API Ìò∏Ï∂ú
      const result = await findPassword(formData.name, formData.email);

      if (result.success) {
        // ÏùºÏπòÌïòÎäî ÏÇ¨Ïö©ÏûêÎ•º Ï∞æÏïòÏùÑ Îïå
        setFoundPassword(result.password);
      } else {
        // ÏùºÏπòÌïòÎäî ÏÇ¨Ïö©ÏûêÎ•º Ï∞æÏßÄ Î™ªÌñàÏùÑ Îïå
        setError(result.message || 'ÏûÖÎ†•ÌïòÏã† Ïù¥Î¶ÑÍ≥º Ïù¥Î©îÏùºÏóê Ìï¥ÎãπÌïòÎäî Í≥ÑÏ†ïÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.');
      }

    } catch (err) {
      if (err.response?.data?.message) {
        setError(err.response.data.message);
      } else {
        setError('ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
      }
      console.error('ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞ ÏóêÎü¨:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleBackToLogin = () => {
    navigate('/login');
  };

  if (foundPassword) {
    return (
      <div className="login-container">
        <div className="login-box">
          <div className="login-header">
            <h2>ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Ï∞æÏïòÏäµÎãàÎã§</h2>
            <p>ÏïÑÎûò Ï†ïÎ≥¥Î•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî</p>
          </div>

          <div style={{ textAlign: 'center', margin: '30px 0' }}>
            <div style={{ 
              fontSize: '3rem', 
              color: '#667eea', 
              marginBottom: '20px' 
            }}>
              üîë
            </div>
            <div style={{ 
              backgroundColor: '#f8f9fa', 
              padding: '20px', 
              borderRadius: '10px',
              marginBottom: '20px'
            }}>
              <p style={{ 
                color: '#666', 
                marginBottom: '10px'
              }}>
                <strong>Ïù¥Î¶Ñ:</strong> {formData.name}
              </p>
              <p style={{ 
                color: '#666', 
                marginBottom: '15px'
              }}>
                <strong>Ïù¥Î©îÏùº:</strong> {formData.email}
              </p>
              <p style={{ 
                color: '#333', 
                fontSize: '1.1rem',
                fontWeight: 'bold'
              }}>
                <strong>ÎπÑÎ∞ÄÎ≤àÌò∏:</strong> {foundPassword}
              </p>
            </div>
            <p style={{ 
              fontSize: '0.85rem', 
              color: '#888' 
            }}>
              Î≥¥ÏïàÏùÑ ÏúÑÌï¥ Î°úÍ∑∏Ïù∏ ÌõÑ ÎπÑÎ∞ÄÎ≤àÌò∏Î•º Î≥ÄÍ≤ΩÌïòÏãúÍ∏∞ Î∞îÎûçÎãàÎã§.
            </p>
          </div>

          <button 
            type="button" 
            className="login-button"
            onClick={handleBackToLogin}
          >
            Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
          </button>

          <div className="login-footer">
            <div className="signup-link">
              <button 
                type="button"
                onClick={() => setFoundPassword('')}
                style={{
                  background: 'none',
                  border: 'none',
                  color: '#667eea',
                  textDecoration: 'underline',
                  cursor: 'pointer',
                  fontSize: '0.9rem',
                  fontWeight: '500'
                }}
              >
                Îã§Î•∏ Í≥ÑÏ†ïÏúºÎ°ú Îã§Ïãú Ï∞æÍ∏∞
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <h2>ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</h2>
          <p>Í∞ÄÏûÖÌïòÏã† Ïù¥Î¶ÑÍ≥º Ïù¥Î©îÏùºÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="name">Ïù¥Î¶Ñ</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="Í∞ÄÏûÖÌïòÏã† Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              required
              disabled={isLoading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Ïù¥Î©îÏùº</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="Í∞ÄÏûÖÌïòÏã† Ïù¥Î©îÏùºÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              required
              disabled={isLoading}
            />
          </div>

          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          <button 
            type="submit" 
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="spinner"></span>
                ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÎäî Ï§ë...
              </>
            ) : (
              'ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞'
            )}
          </button>
        </form>

        <div className="login-footer">
          <div className="signup-link">
            <span>Ïù¥ÎØ∏ Í≥ÑÏ†ï Ï†ïÎ≥¥Î•º Í∏∞ÏñµÌïòÏÖ®ÎÇòÏöî? </span>
            <button 
              type="button"
              onClick={handleBackToLogin}
              style={{
                background: 'none',
                border: 'none',
                color: '#667eea',
                textDecoration: 'underline',
                cursor: 'pointer',
                fontSize: '0.9rem',
                fontWeight: '500'
              }}
            >
              Î°úÍ∑∏Ïù∏
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FindPasswordPage;
