import React, { useContext } from "react";
import {
  BrowserRouter as Router,
  NavLink,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import "bootstrap-icons/font/bootstrap-icons.css";

import "./App.css";

import SearchPage from "./components/Pages/SearchPage";
import Home from "./components/Pages/Home";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import MovieDetailPage from "./components/Pages/MovieDetailPage";
import BoardListPage from "./components/Pages/BoardListPage";
import BoardDetailPage from "./components/Pages/BoardDetailPage";
import Navbar from "./components/home/Navbar";

import { AuthProvider, AuthContext } from "./components/context/AuthContext";

// Protected Route Component
function ProtectedRoute({ children }) {
  const { user, loading } = useContext(AuthContext);

  if (loading) {
    return <div className="text-center p-5">Loading...</div>;
  }

  return user ? children : <Navigate to="/login" />;
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="container-fluid px-0 bg-dark min-vh-100">
          <div className="row g-0">
            <div className="col-12">
              <div className="row g-0">
                <div className="col-12">
                  <h1 id="logo" className="text-center py-3">
                    <NavLink to="/" className="text-white text-decoration-none">
                      영화 리뷰 사이트
                    </NavLink>
                  </h1>
                </div>
              </div>
              <div className="row g-0">
                <div className="col-12">
                  <Navbar />
                </div>
              </div>
              <div className="row g-0">
                <div className="col-12">
                  <div className="content-area">
                    <Routes>
                      <Route path="/" element={<Home />} />
                      <Route path="/login" element={<Login />} />
                      <Route path="/register" element={<Register />} />
                      <Route path="/search/*" element={<SearchPage />} />
                      <Route
                        path="/movie/:movieId"
                        element={<MovieDetailPage />}
                      />
                      <Route path="/boards" element={<BoardListPage />} />
                      <Route
                        path="/boards/:boardId"
                        element={<BoardDetailPage />}
                      />
                    </Routes>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
